import React, {useState} from 'react';
import ObjectListItem from "./ObjectListItem";
import AddObject from "./AddObject";
import Modal from "../Modal/modal";

const testOrderList = []


const ObjectPage = () => {
    const [modalVisible, setModalVisible] = useState(false)

    const [orderList, setOrderList] = useState(testOrderList)

    const addOrder = (order, isNew) => {
        if(isNew !== -1) {
            setOrderList([...orderList, order])
        } else {
            let arr = [...orderList]
            arr[isNew] = order
            setOrderList([...arr])
        }
    }

    const printOrders = () => {
        if(orderList.length !==0) {
            return   orderList.map((order, index) =>
                <div key={"order:" + index}>
                    <ObjectListItem order={order}/>
                </div>
            )
        } return <div className={'d-flex justify-content-center rounded-3 p-3 w-100 mt-2'} style={{border:"solid 1px"}}>Список объектов пуст!</div>
    }

    return (
        <div className="px-3 pt-3">
            <div>
                <button className="border-2 border-black rounded-3 " style={{background: "#91FCA2"}}
                        onClick={() => setModalVisible(true)}>
                    <img width={48} height={51} src={"./files/add.svg"}/>
                    <span>Добавить объект</span>
                </button>
            </div>

            <div>
                <Modal
                    visible={modalVisible}
                    setVisible={setModalVisible}
                    children={<AddObject  setModalVisible={setModalVisible} addOrder={addOrder}/>}
                />
            </div>

            <div style={{height: "600px", overflowY: "auto"}}>
                {printOrders()}
            </div>
        </div>
    );
};

export default ObjectPage;
